                {
                  "node": {
                    "title": "A New Hope"
                  }
                },
                {
                  "node": {
                    "title": "The Empire Strikes Back"
                  }
                },
                {
                  "node": {
                    "title": "Return of the Jedi"
                  }
                },
                {
                  "node": {
                    "title": "Revenge of the Sith"
                  }
                },
                {
                  "node": {
                    "title": "The Force Awakens"
                  }
                }
              ]
            }
          }
        },
        {
          "node": {
            "name": "C-3PO",
            "gender": "n/a",
            "homeworld": {
              "name": "Tatooine"
            },
            "filmConnection": {
              "edges": [
                {
                  "node": {
                    "title": "A New Hope"
                  }
                },
                {
                  "node": {
                    "title": "The Empire Strikes Back"
                  }
                },
                {
                  "node": {
                    "title": "Return of the Jedi"
                  }
                },
                {
                  "node": {
