        'favorites/create',
        { id: randomTweet.id_str },
        function (err, response) {
          // if there was an error while 'favorite'
          if (err) {
            console.log('CANNOT BE FAVORITE... Error');
          } else {
            console.log('FAVORITED... Success!!!');
          }
        }
      );
    }
  });
};
